import Swiper from"https://cdn.jsdelivr.net/npm/swiper@11/swiper-bundle.min.mjs";const swiperPromo=new Swiper("#swiper-promo",{lazy:!0,direction:"horizontal",loop:!0,pagination:{el:".swiper-pagination",clickable:!0,renderBullet:function(e,i){return'<span class="'+i+'"></span>'}},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},on:{slideChange:function(){const e=this.slides[this.activeIndex].querySelector(".swiper-lazy-preloader");e.style.display="flex",setTimeout((()=>{e.style.display="none"}),500)}}}),swiperAdv=new Swiper("#swiper-adv",{direction:"horizontal",loop:!0});